# frozen_string_literal: true

require 'helpers/thumbnail/thumbnail_creator'
require 'base64'
require 'fileutils'

describe ThumbnailCreator do
  context '-> thumbnail' do
    describe '-> create' do
      it '-> successfully' do
        attachment_infos = create_attachment
        tempfile = create_tmp_image
        assert(!attachment_infos[0].thumb)

        creator = described_class.new(ThumbnailerMock.new)
        result = creator.create_derivative(tempfile.path, nil, nil, {}, attachment_infos[0])

        assert(attachment_infos[0].thumb)
        expected_path = File.dirname(tempfile) + '/' + attachment_infos[1] + '.thumb.jpg'
        assert_equal(result[:thumbnail].path, expected_path)
      end
    end
  end
end

class ThumbnailerMock
  def create_thumbnail(tmp_path)
    File.open(tmp_path)
  end
end

def create_attachment
  uuid = SecureRandom.uuid
  attachment = Attachment.new(
    bucket: 1,
    filename: 'test',
    file_path: 'tmp',
    created_by: 1,
    created_for: 1,
    content_type: 'svg',
    attachable_type: 1,
    attachable_id: 1
  )
  attachment[:identifier] = uuid
  [attachment, uuid]
end

def create_tmp_image
  # An image base64 string
  data = ''
  tempfile = Tempfile.new('example.png')
  File.open(tempfile.path, 'wb') do |f|
    f.write(Base64.decode64(data))
  end

  tempfile
end
